import React from "react";
import { Redirect } from "react-router-dom";
import { authStates, withAuth } from "../components/auth";
import { signOut } from "../utils/firebase";
import Loader from "../components/loader";
import { FaBars, FaTimes } from 'react-icons/fa';

function handleSignOut() {
  signOut()
    .then(() => {
      console.log("Signed Out");
    })
    .catch(e => {
      console.log("Error signing out", e);
    });
}

class Home extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      showMenu: false,
    };
  }

  toggleMenu = () => {
    this.setState({ showMenu: !this.state.showMenu });
  };

  render() {
    if (this.props.authState === authStates.INITIAL_VALUE) {
      return <Loader />;
    }

    if (this.props.authState === authStates.LOGGED_OUT) {
      return <Redirect to="/login"></Redirect>;
    }

    return (
      <div className="container">
        <div className="navbar">
          <div className="padding80">
            <button className="menu-button" onClick={this.toggleMenu}>
              {this.state.showMenu ? <FaTimes /> : <FaBars />}
            </button>
            <p>Welcome, let's fly!</p>
            {this.state.showMenu && (
              <div className="navbar-items">
                {/* Add your other navigation links here */}
                <button onClick={handleSignOut}> Sign Out </button>
              </div>
            )}
          </div>
        </div>
      </div>
    );
  }
}

export default withAuth(Home);
